html {
  font-size: 1px;
  /*for using REM units*/
}
body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
  font-size: 16rem;
  line-height: 1.3;
  color: #fff;
}
h1 {
  font-size: 48rem;
  font-weight: 600;
}
h2 {
  font-size: 24rem;
  font-weight: 400;
}
p {
  color: #ececec;
  font-size: 15rem;
  font-weight: 150;
}
.text__body {
  margin-bottom: 25px;
}
.text__subtitle {
  margin-bottom: 25px;
}
.text__subtitle--highlight {
  color: #ff00ff;
}
.container {
  padding: 3em 2em;
  scroll-snap-align: center;
}
.container--center {
  text-align: center;
}
.container--fill {
  background: #222227;
}
.container--highlight {
  border-top: 1px solid #ff00ff;
}
img {
  width: 100%;
  max-height: 450px;
  object-fit: cover;
  object-position: 0 60%;
}
.ico {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 4em;
  height: 4em;
  background: #ff00ff;
  border-radius: 50%;
  margin-bottom: 25px;
}
.ico--center {
  margin: 0 auto 25px auto;
}
@media screen and (max-width: 800px) {
  .ico {
    margin: 0 auto 25px auto;
  }
}
.logo {
  display: inline-block;
  margin-right: 15px;
  margin-top: 15px;
  height: 20px;
  width: 20px;
}
.col {
  display: grid;
  grid-template-columns: minmax(250px, 1250px);
  grid-template-rows: minmax(min-content, max-content);
  grid-gap: 150px;
  justify-content: center;
  background: #0f0f10;
}
.row {
  display: grid;
  grid-gap: 25px;
  align-items: center;
  justify-content: space-between;
}
.row--logo {
  justify-content: center;
  align-items: center;
}
.row--text {
  grid-template-rows: minmax(10px, max-content);
  grid-template-columns: 1fr;
  text-align: center;
}
.row--2 {
  grid-template-columns: repeat(2, minmax(min-content, 1fr));
  grid-template-rows: repeat(minmax(min-content, 1fr));
  margin: 0;
}
.row--3 {
  grid-template-columns: repeat(3, minmax(min-content, 1fr));
  grid-template-rows: auto;
  padding: 0px;
}
.row--scroll {
  padding: 0px;
}
@media screen and (max-width: 800px) {
  .row {
    text-align: center;
    grid-gap: 0;
  }
  .row--2 {
    grid-template-columns: 1fr;
  }
  .row--scroll {
    grid-template-columns: repeat(4, minmax(100%, max-content));
    overflow-x: scroll;
    scroll-snap-type: x mandatory;
  }
}
form {
  width: 100%;
  max-width: 550px;
  margin: 0 auto;
}
.input {
  color: #fbfbfb;
  outline: 1px solid #fbfbfb;
  border: none;
  background: none;
  width: 100%;
  height: 64rem;
  padding: 0 25px;
  transition: outline 275ms linear;
  margin-bottom: 25px;
}
.input:focus {
  outline: 1px solid #ff00ff;
}
.btn {
  width: 100%;
  height: 64rem;
  background: #fbfbfb;
  border: none;
  -webkit-appearance: none;
}
.btn--primary {
  background: #fbfbfb;
}
.fade-in {
  opacity: 0;
  transition-property: opacity, transform;
  transition-duration: 250ms;
  transform: translatey(50px);
  transition-timing-function: ease-in-out;
}
.fade-in.appear {
  opacity: 1;
  transform: translateY(0px);
}

